    data: <<~XSLT
<?xml version="1.0" encoding="UTF-8"?>
<xsl:stylesheet version="1.0"
    xmlns:xsl="http://www.w3.org/1999/XSL/Transform"
    xmlns:egonp="http://schemas.gov.sk/form/00166073.RESS_Exekucne_konanie_Navrh_na_vykonanie_exekucie.sk/1.24">
    <xsl:output method="text" indent="yes" omit-xml-declaration="yes"/>
    <xsl:strip-space elements="*" />
    <xsl:template match="/">
        <xsl:text>Exekučné konanie - Návrh na vykonanie exekúcie</xsl:text>
        <xsl:text>&#xA;</xsl:text>
        <xsl:call-template name="Nazov_2"></xsl:call-template>
        <xsl:call-template name="Sekcia_SudnyExekutor"></xsl:call-template>
        <xsl:call-template name="Asekcia_ucastniciKonaniaOpravneny"></xsl:call-template>
        <xsl:call-template name="SekciaBmaNema"></xsl:call-template>
        <xsl:if test="/egonp:ProposalForExecution/egonp:CollectiveRepresentative">
            <xsl:call-template name="Sekcia_MaZastupcu_1"></xsl:call-template>
        </xsl:if>
        <xsl:call-template name="Csekcia_ucastniciKonaniaPovinny"></xsl:call-template>
        <xsl:call-template name="Dsekcia_ExekucnyTitul"></xsl:call-template>
        <xsl:choose>
            <xsl:when test="not(/egonp:ProposalForExecution/egonp:EnforcementOfClaims)">
                <xsl:call-template name="Esekcia_VymahanyNarok" />
            </xsl:when>
            <xsl:otherwise>
                <xsl:if test="/egonp:ProposalForExecution/egonp:EnforcementOfClaims">
                    <xsl:call-template name="Esekcia_VymahanyNarok"></xsl:call-template>
                </xsl:if>
            </xsl:otherwise>
        </xsl:choose>
        <xsl:choose>
            <xsl:when test="not(/egonp:ProposalForExecution/egonp:CostsOfExecution)">
                <xsl:call-template name="Fsekcia_TrovyExekucie" />
            </xsl:when>
            <xsl:otherwise>
                <xsl:if test="/egonp:ProposalForExecution/egonp:CostsOfExecution">
                    <xsl:call-template name="Fsekcia_TrovyExekucie"></xsl:call-template>
                </xsl:if>
            </xsl:otherwise>
        </xsl:choose>
        <xsl:choose>
            <xsl:when test="not(/egonp:ProposalForExecution/egonp:ExemptionFromCourtFees)">
                <xsl:call-template name="Gsekcia_OslobodenieOdSudnehoPoplatku" />
            </xsl:when>
            <xsl:otherwise>
                <xsl:if test="/egonp:ProposalForExecution/egonp:ExemptionFromCourtFees">
                    <xsl:call-template name="Gsekcia_OslobodenieOdSudnehoPoplatku"></xsl:call-template>
                </xsl:if>
            </xsl:otherwise>
        </xsl:choose>
        <xsl:choose>
            <xsl:when test="not(/egonp:ProposalForExecution/egonp:StatementsAndProposal)">
                <xsl:call-template name="Hsekcia_VyhlaseniaNavrh" />
            </xsl:when>
            <xsl:otherwise>
                <xsl:if test="/egonp:ProposalForExecution/egonp:StatementsAndProposal">
                    <xsl:call-template name="Hsekcia_VyhlaseniaNavrh"></xsl:call-template>
                </xsl:if>
            </xsl:otherwise>
        </xsl:choose>
        <xsl:choose>
            <xsl:when test="not(/egonp:ProposalForExecution/egonp:SPRAttachments)">
                <xsl:call-template name="Isekcia_prilohySpr" />
            </xsl:when>
            <xsl:otherwise>
                <xsl:if test="/egonp:ProposalForExecution/egonp:SPRAttachments">
                    <xsl:call-template name="Isekcia_prilohySpr"></xsl:call-template>
                </xsl:if>
            </xsl:otherwise>
        </xsl:choose>
        <xsl:call-template name="SekOznacenieMiesta"></xsl:call-template>
        <xsl:if test="/egonp:ProposalForExecution/egonp:Attachments/egonp:Attachment">
            <xsl:call-template name="Hsekcia_Prilohy"></xsl:call-template>
        </xsl:if>
    </xsl:template>
    <xsl:template name="Nazov_2"></xsl:template>
    <xsl:template name="Sekcia_SudnyExekutor">
        <xsl:text>&#09;</xsl:text>
        <xsl:text>Spôsob podania návrhu</xsl:text>
        <xsl:text>&#xA;</xsl:text>
        <xsl:if test="/egonp:ProposalForExecution/egonp:ProposalMethod/egonp:Executor/text()">
            <xsl:text>&#09;</xsl:text>
            <xsl:text>&#09;</xsl:text>
            <xsl:text>Návrh sa podáva prostredníctvom súdneho exekútora:</xsl:text>
            <xsl:call-template name="booleanCheckboxToString">
                <xsl:with-param name="boolValue" select="/egonp:ProposalForExecution/egonp:ProposalMethod/egonp:Executor" />
            </xsl:call-template>
            <xsl:text>&#xA;</xsl:text>
        </xsl:if>
        <xsl:if test="/egonp:ProposalForExecution/egonp:ProposalMethod/egonp:PhysicalPersonData/egonp:PersonData/egonp:PhysicalPerson/egonp:PersonName/egonp:GivenName or /egonp:ProposalForExecution/egonp:ProposalMethod/egonp:PhysicalPersonData/egonp:PersonData/egonp:PhysicalPerson/egonp:PersonName/egonp:Affix[@type='qualification' and @position='prefix'] or /egonp:ProposalForExecution/egonp:ProposalMethod/egonp:PhysicalPersonData/egonp:PersonData/egonp:PhysicalPerson/egonp:PersonName/egonp:FamilyName or /egonp:ProposalForExecution/egonp:ProposalMethod/egonp:PhysicalPersonData/egonp:PersonData/egonp:PhysicalPerson/egonp:PersonName/egonp:Affix[@type='qualification' and @position='postfix'] or /egonp:ProposalForExecution/egonp:ProposalMethod/egonp:EvidenceNumber">
            <xsl:call-template name="Sekcia_MenoPriezviskoExekutor"></xsl:call-template>
        </xsl:if>
    </xsl:template>
    <xsl:template name="Asekcia_ucastniciKonaniaOpravneny">
        <xsl:text>&#09;</xsl:text>
        <xsl:text>A) Účastníci konania a ich zákonní zástupcovia – oprávnení</xsl:text>
        <xsl:text>&#xA;</xsl:text>
        <xsl:choose>
            <xsl:when test="not(/egonp:ProposalForExecution/egonp:LegitimatePerson)">
                <xsl:call-template name="UcastnikKonaniaOpravneny" />
            </xsl:when>
            <xsl:otherwise>
                <xsl:for-each select="/egonp:ProposalForExecution/egonp:LegitimatePerson">
                    <xsl:call-template name="UcastnikKonaniaOpravneny"></xsl:call-template>
                </xsl:for-each>
            </xsl:otherwise>
        </xsl:choose>
    </xsl:template>
    <xsl:template name="SekciaBmaNema">
        <xsl:if test="/egonp:ProposalForExecution/egonp:HasCommonRepresentative/text()">
            <xsl:text>&#09;</xsl:text>
            <xsl:text>Oprávnený má právneho zástupcu:</xsl:text>
            <xsl:call-template name="booleanCheckboxToString">
                <xsl:with-param name="boolValue" select="/egonp:ProposalForExecution/egonp:HasCommonRepresentative" />
            </xsl:call-template>
            <xsl:text>&#xA;</xsl:text>
        </xsl:if>
    </xsl:template>
    <xsl:template name="Sekcia_MaZastupcu_1">
        <xsl:text>&#09;</xsl:text>
        <xsl:text>B) Právny zástupca (spoločný zástupca)</xsl:text>
        <xsl:text>&#xA;</xsl:text>
        <xsl:call-template name="SekciaZastupcaZalobcuFoPo_1"></xsl:call-template>
        <xsl:if test="/egonp:ProposalForExecution/egonp:CollectiveRepresentative/egonp:CommonRepresentative">
            <xsl:call-template name="spolocnyZastupcaPomSekcia"></xsl:call-template>
        </xsl:if>
        <xsl:if test="/egonp:ProposalForExecution/egonp:CollectiveRepresentative/egonp:Lawyer">
            <xsl:call-template name="advokatZalobcaPom_1"></xsl:call-template>
        </xsl:if>
        <xsl:if test="/egonp:ProposalForExecution/egonp:CollectiveRepresentative/egonp:PhysicalPersonData or /egonp:ProposalForExecution/egonp:CollectiveRepresentative/egonp:CorporateBodyData or /egonp:ProposalForExecution/egonp:LegitimatePerson/egonp:BankConnection or /egonp:ProposalForExecution/egonp:CollectiveRepresentative/egonp:BankConnection/egonp:InternationalBankConnection/egonp:IBAN or /egonp:ProposalForExecution/egonp:CollectiveRepresentative/egonp:BankConnection/egonp:InternationalBankConnection/egonp:BIC or /egonp:ProposalForExecution/egonp:CollectiveRepresentative/egonp:BankConnection/egonp:VariableSymbol or /egonp:ProposalForExecution/egonp:CollectiveRepresentative/egonp:BankConnection/egonp:SpecificSymbol">
            <xsl:call-template name="Sekcia_ZastupcaZalobca_1"></xsl:call-template>
        </xsl:if>
    </xsl:template>
    <xsl:template name="Csekcia_ucastniciKonaniaPovinny">
        <xsl:text>&#09;</xsl:text>
        <xsl:text>C) Účastníci konania a ich zákonní zástupcovia – povinní</xsl:text>
        <xsl:text>&#xA;</xsl:text>
        <xsl:choose>
            <xsl:when test="not(/egonp:ProposalForExecution/egonp:ObligatoryPerson)">
                <xsl:call-template name="Zalovany" />
            </xsl:when>
            <xsl:otherwise>
                <xsl:for-each select="/egonp:ProposalForExecution/egonp:ObligatoryPerson">
                    <xsl:call-template name="Zalovany"></xsl:call-template>
                </xsl:for-each>
            </xsl:otherwise>
        </xsl:choose>
    </xsl:template>
    <xsl:template name="Dsekcia_ExekucnyTitul">
        <xsl:text>&#09;</xsl:text>
        <xsl:text>D) Exekučný titul</xsl:text>
        <xsl:text>&#xA;</xsl:text>
        <xsl:choose>
            <xsl:when test="not(/egonp:ProposalForExecution/egonp:ExecutionTitle)">
                <xsl:call-template name="exekucnyTitulOpak" />
            </xsl:when>
            <xsl:otherwise>
                <xsl:for-each select="/egonp:ProposalForExecution/egonp:ExecutionTitle">
                    <xsl:call-template name="exekucnyTitulOpak"></xsl:call-template>
                </xsl:for-each>
            </xsl:otherwise>
        </xsl:choose>
    </xsl:template>
    <xsl:template name="Esekcia_VymahanyNarok">
        <xsl:text>&#09;</xsl:text>
        <xsl:text>E) Vymáhaný nárok</xsl:text>
        <xsl:text>&#xA;</xsl:text>
        <xsl:if test="/egonp:ProposalForExecution/egonp:EnforcementOfClaims/egonp:KindOfClaims/egonp:Codelist[egonp:CodelistCode='64']/egonp:CodelistItem/egonp:ItemName[@Language='sk']/text()">
            <xsl:text>&#09;</xsl:text>
            <xsl:text>&#09;</xsl:text>
            <xsl:text>Vymáhaný nárok:</xsl:text>
            <xsl:value-of select="/egonp:ProposalForExecution/egonp:EnforcementOfClaims/egonp:KindOfClaims/egonp:Codelist[egonp:CodelistCode='64']/egonp:CodelistItem/egonp:ItemName[@Language='sk']"/>
            <xsl:text>&#xA;</xsl:text>
        </xsl:if>
        <xsl:if test="/egonp:ProposalForExecution/egonp:EnforcementOfClaims/egonp:MoneyClaim">
            <xsl:call-template name="penaznyNarokSekcia"></xsl:call-template>
        </xsl:if>
        <xsl:if test="/egonp:ProposalForExecution/egonp:EnforcementOfClaims/egonp:Alimony">
            <xsl:call-template name="penaznyNarokVyzivneSekcia"></xsl:call-template>
        </xsl:if>
        <xsl:if test="/egonp:ProposalForExecution/egonp:EnforcementOfClaims/egonp:NotMoneyClaim">
            <xsl:call-template name="nepenaznyNarokSekcia"></xsl:call-template>
        </xsl:if>
    </xsl:template>
    <xsl:template name="Fsekcia_TrovyExekucie">
        <xsl:text>&#09;</xsl:text>
        <xsl:text>F) Trovy exekúcie - oprávnený</xsl:text>
        <xsl:text>&#xA;</xsl:text>
        <xsl:if test="/egonp:ProposalForExecution/egonp:CostsOfExecution/egonp:CourtFee/text()">
            <xsl:text>&#09;</xsl:text>
            <xsl:text>&#09;</xsl:text>
            <xsl:text>Súdny poplatok (návrh na vykonanie exekúcie):</xsl:text>
            <xsl:call-template name="booleanCheckboxToString">
                <xsl:with-param name="boolValue" select="/egonp:ProposalForExecution/egonp:CostsOfExecution/egonp:CourtFee" />
            </xsl:call-template>
            <xsl:text>&#xA;</xsl:text>
        </xsl:if>
        <xsl:if test="/egonp:ProposalForExecution/egonp:CostsOfExecution/egonp:AmountOfCourtFee">
            <xsl:call-template name="SekciaSudnyPoplatok"></xsl:call-template>
        </xsl:if>
        <xsl:if test="/egonp:ProposalForExecution/egonp:CostsOfExecution/egonp:ConversionFee">
            <xsl:call-template name="sek_PoplatokZaKonverziu"></xsl:call-template>
        </xsl:if>
        <xsl:call-template name="SekciaTrovy_1"></xsl:call-template>
        <xsl:if test="/egonp:ProposalForExecution/egonp:CostsOfExecution/egonp:CostsForDraftingTheProposal/text()">
            <xsl:text>&#09;</xsl:text>
            <xsl:text>&#09;</xsl:text>
            <xsl:text>Trovy za spísanie návrhu na vykonanie exekúcie u súdneho exekútora:</xsl:text>
            <xsl:call-template name="booleanCheckboxToString">
                <xsl:with-param name="boolValue" select="/egonp:ProposalForExecution/egonp:CostsOfExecution/egonp:CostsForDraftingTheProposal" />
            </xsl:call-template>
            <xsl:text>&#xA;</xsl:text>
        </xsl:if>
        <xsl:if test="/egonp:ProposalForExecution/egonp:CostsOfExecution/egonp:AmountOfCostsForDraftingTheProposal">
            <xsl:call-template name="SekciaVyska_2"></xsl:call-template>
        </xsl:if>
        <xsl:if test="/egonp:ProposalForExecution/egonp:CostsOfExecution/egonp:OtherCosts/text()">
            <xsl:text>&#09;</xsl:text>
            <xsl:text>&#09;</xsl:text>
            <xsl:text>Iné trovy:</xsl:text>
            <xsl:call-template name="booleanCheckboxToString">
                <xsl:with-param name="boolValue" select="/egonp:ProposalForExecution/egonp:CostsOfExecution/egonp:OtherCosts" />
            </xsl:call-template>
            <xsl:text>&#xA;</xsl:text>
        </xsl:if>
        <xsl:call-template name="sec_ine_trovy"></xsl:call-template>
        <xsl:call-template name="SekciaTrovy_3"></xsl:call-template>
        <xsl:if test="/egonp:ProposalForExecution/egonp:CostsOfExecution/egonp:DescriptionOfCosts/text()">
            <xsl:text>&#09;</xsl:text>
            <xsl:text>&#09;</xsl:text>
            <xsl:text>Odôvodnenie trov:</xsl:text>
            <xsl:call-template name="string-replace-all">
                <xsl:with-param name="text" select="/egonp:ProposalForExecution/egonp:CostsOfExecution/egonp:DescriptionOfCosts" />
                <xsl:with-param name="replace" select="'&#10;'" />
                <xsl:with-param name="by" select="'&#13;&#10;&#09;
&#09;
'" />
            </xsl:call-template>
            <xsl:text>&#xA;</xsl:text>
        </xsl:if>
    </xsl:template>
    <xsl:template name="Gsekcia_OslobodenieOdSudnehoPoplatku">
        <xsl:text>&#09;</xsl:text>
        <xsl:text>G) Oslobodenie od súdneho poplatku</xsl:text>
        <xsl:text>&#xA;</xsl:text>
        <xsl:if test="/egonp:ProposalForExecution/egonp:ExemptionFromCourtFees/egonp:ItIsExempted/text()">
            <xsl:text>&#09;</xsl:text>
            <xsl:text>&#09;</xsl:text>
            <xsl:text>Oslobodenie od súdneho poplatku:</xsl:text>
            <xsl:call-template name="booleanCheckboxToString">
                <xsl:with-param name="boolValue" select="/egonp:ProposalForExecution/egonp:ExemptionFromCourtFees/egonp:ItIsExempted" />
            </xsl:call-template>
            <xsl:text>&#xA;</xsl:text>
        </xsl:if>
        <xsl:if test="/egonp:ProposalForExecution/egonp:ExemptionFromCourtFees/egonp:Proposal/egonp:Codelist[egonp:CodelistCode='51']/egonp:CodelistItem/egonp:ItemCode or /egonp:ProposalForExecution/egonp:ExemptionFromCourtFees/egonp:Proposal/egonp:Codelist[egonp:CodelistCode='51']/egonp:CodelistItem/egonp:ItemName[@Language='sk'] or /egonp:ProposalForExecution/egonp:ExemptionFromCourtFees/egonp:Reasons">
            <xsl:call-template name="oslobodenieOdSUdnehoPoplatkuPom"></xsl:call-template>
        </xsl:if>
    </xsl:template>
    <xsl:template name="Hsekcia_VyhlaseniaNavrh">
        <xsl:text>&#09;</xsl:text>
        <xsl:text>H) Vyhlásenia a návrh</xsl:text>
        <xsl:text>&#xA;</xsl:text>
        <xsl:if test="/egonp:ProposalForExecution/egonp:StatementsAndProposal/egonp:TheMandateForRecovery/text()">
            <xsl:text>&#09;</xsl:text>
            <xsl:text>&#09;</xsl:text>
            <xsl:text>Oprávnený vyhlasuje, že nárok podľa exekučného titulu povinný ku dňu podania návrhu na vykonanie exekúcie dobrovoľne nesplnil:</xsl:text>
            <xsl:call-template name="booleanCheckboxToString">
                <xsl:with-param name="boolValue" select="/egonp:ProposalForExecution/egonp:StatementsAndProposal/egonp:TheMandateForRecovery" />
            </xsl:call-template>
            <xsl:text>&#xA;</xsl:text>
        </xsl:if>
        <xsl:if test="/egonp:ProposalForExecution/egonp:StatementsAndProposal/egonp:TheMandateForRecoveryDetail">
            <xsl:call-template name="SekciaPodrobnosti"></xsl:call-template>
        </xsl:if>
        <xsl:if test="/egonp:ProposalForExecution/egonp:StatementsAndProposal/egonp:ThereIsNoOtherProceedings/text()">
            <xsl:text>&#09;</xsl:text>
            <xsl:text>&#09;</xsl:text>
            <xsl:text>Oprávnený vyhlasuje, že na podklade exekučného titulu uvedeného v časti D) ohľadne toho istého nároku iné exekučné konanie alebo obdobné vykonávacie konanie:</xsl:text>
            <xsl:call-template name="booleanCheckboxToString">
                <xsl:with-param name="boolValue" select="/egonp:ProposalForExecution/egonp:StatementsAndProposal/egonp:ThereIsNoOtherProceedings" />
            </xsl:call-template>
            <xsl:text>&#xA;</xsl:text>
        </xsl:if>
        <xsl:if test="/egonp:ProposalForExecution/egonp:StatementsAndProposal/egonp:ThereIsNoOtherProceedingsDetail">
            <xsl:call-template name="SekciaPodrobnosti_2"></xsl:call-template>
        </xsl:if>
        <xsl:if test="/egonp:ProposalForExecution/egonp:StatementsAndProposal/egonp:InAnotherPartOfTheEnforcementProceedings/text()">
            <xsl:text>&#09;</xsl:text>
            <xsl:text>&#09;</xsl:text>
            <xsl:text>Časť nároku podľa exekučného titulu je vymáhaná v inom exekučnom konaní alebo obdobnom vykonávacom konaní:</xsl:text>
            <xsl:call-template name="booleanCheckboxToString">
                <xsl:with-param name="boolValue" select="/egonp:ProposalForExecution/egonp:StatementsAndProposal/egonp:InAnotherPartOfTheEnforcementProceedings" />
            </xsl:call-template>
            <xsl:text>&#xA;</xsl:text>
        </xsl:if>
        <xsl:if test="/egonp:ProposalForExecution/egonp:StatementsAndProposal/egonp:InAnotherPartOfTheEnforcementProceedingsDetail">
            <xsl:call-template name="SekciaPodrobnosti_3"></xsl:call-template>
        </xsl:if>
        <xsl:if test="/egonp:ProposalForExecution/egonp:StatementsAndProposal/egonp:ClaimFromOldExecution/text()">
            <xsl:text>&#09;</xsl:text>
            <xsl:text>&#09;</xsl:text>
            <xsl:text>Návrhom sa uplatňuje nárok zo starej exekúcie:</xsl:text>
            <xsl:call-template name="booleanCheckboxToString">
                <xsl:with-param name="boolValue" select="/egonp:ProposalForExecution/egonp:StatementsAndProposal/egonp:ClaimFromOldExecution" />
            </xsl:call-template>
            <xsl:text>&#xA;</xsl:text>
        </xsl:if>
        <xsl:if test="/egonp:ProposalForExecution/egonp:StatementsAndProposal/egonp:ClaimFromOldExecutionDetail">
            <xsl:call-template name="SekciaPodrobnosti_8"></xsl:call-template>
        </xsl:if>
        <xsl:if test="/egonp:ProposalForExecution/egonp:StatementsAndProposal/egonp:EnforcementOfClaimsByThirdParties/text()">
            <xsl:text>&#09;</xsl:text>
            <xsl:text>&#09;</xsl:text>
            <xsl:text>Oprávnený je oprávnený vyžadovať splnenie vymáhaného nároku zároveň od iných osôb:</xsl:text>
            <xsl:call-template name="booleanCheckboxToString">
                <xsl:with-param name="boolValue" select="/egonp:ProposalForExecution/egonp:StatementsAndProposal/egonp:EnforcementOfClaimsByThirdParties" />
            </xsl:call-template>
            <xsl:text>&#xA;</xsl:text>
        </xsl:if>
        <xsl:if test="/egonp:ProposalForExecution/egonp:StatementsAndProposal/egonp:EnforcementOfClaimsByThirdPartiesDetail">
            <xsl:call-template name="SekciaPodrobnosti_4"></xsl:call-template>
        </xsl:if>
        <xsl:if test="/egonp:ProposalForExecution/egonp:StatementsAndProposal/egonp:IsLegalSuccessor/text()">
