				<xsl:text>Áno</xsl:text>
			</xsl:when>
			<xsl:when test="$boolValueString = 'false' ">
				<xsl:text>Nie</xsl:text>
			</xsl:when>
			<xsl:when test="$boolValueString = '1' ">
				<xsl:text>Áno</xsl:text>
			</xsl:when>
			<xsl:when test="$boolValueString = '0' ">
				<xsl:text>Nie</xsl:text>
			</xsl:when>
			<xsl:otherwise>
				<xsl:value-of select="$boolValueString"></xsl:value-of>
			</xsl:otherwise>
		</xsl:choose>
	</xsl:template>
