            if ($(obj).val().search(/(.*)[\.\,][0-9][0-9][0-9][0-9][0-9]/) === 0 && newVal.length > $(obj).val().length)
                return true;
        }
        else if (n == 1) {
            if ($(obj).val().search(/(.*)[\.\,][0-9]/) === 0 && newVal.length > $(obj).val().length)
                return true;
        }
    }
    return false;
}


